import styled from "styled-components";

export const Testimonials = () => {
  return (
    <StyledWrapper>
      <section className="pn_reviews my-5">
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <h1 className="text-center pb-5">
                What our Athletics has to say
              </h1>
